'From Cuis 2.9 of 5 November 2010 [latest update: #634] on 18 November 2010 at 8:54:49 am'!!Browser methodsFor: 'class functions' stamp: 'cbr 11/15/2010 19:44'!buildMorphicSwitches	| instanceSwitch commentSwitch classSwitch row aColor |	instanceSwitch := PluggableButtonMorph 				model: self				stateGetter: #instanceMessagesIndicated				action: #indicateInstanceMessages.	instanceSwitch		label: 'instance';		askBeforeChanging: true;		borderWidth: 0.	commentSwitch := PluggableButtonMorph 				model: self				stateGetter: #classCommentIndicated				action: #plusButtonHit.	commentSwitch		label: '?' asText allBold;		askBeforeChanging: true;		setBalloonText: 'class comment';		borderWidth: 0.	classSwitch := PluggableButtonMorph 				model: self				stateGetter: #classMessagesIndicated				action: #indicateClassMessages.	classSwitch		label: 'class';		askBeforeChanging: true;		borderWidth: 0.	row _ AlignmentMorph proportional.	row		addMorph: instanceSwitch fullFrame: (LayoutFrame fractions: (0@0 corner: 0.5@1));		addMorph: commentSwitch fullFrame: (LayoutFrame fractions: (0.51@0 corner: 0.65@1));		addMorph: classSwitch fullFrame: (LayoutFrame fractions: (0.66@0 corner: 1@1)).	aColor := Color colorFrom: self class windowColor.	row color: aColor duller.	"ensure matching button divider color. (see #paneColor)"	aColor := aColor muchLighter.	{ 		instanceSwitch.		commentSwitch.		classSwitch} do: [:m | 				m					color: aColor;					onColor: ColorTheme current switchOn					offColor: ColorTheme current switchOff].	^row! !!Browser methodsFor: 'initialize-release' stamp: 'cbr 11/7/2010 22:37'!buildMorphicSystemCatList	| dragNDropFlag myCatList |	dragNDropFlag := false.	(myCatList := PluggableListMorph new)		model: self		listGetter: #systemCategoryList		indexGetter: #systemCategoryListIndex		indexSetter: #systemCategoryListIndex:		menuGetter: #systemCategoryMenu:		keystrokeAction: #systemCatListKey:from:.	myCatList enableDragNDrop: dragNDropFlag.	^myCatList! !!Browser methodsFor: 'initialize-release' stamp: 'cbr 11/13/2010 10:51'!openAsMorphEditing: editString	"Create a pluggable version of all the morphs for a Browser in Morphic"	| window hSepFrac |	hSepFrac _ 0.4.	window _ SystemWindow new model: self.	"The method SystemWindow>>addMorph:fullFrame: checks scrollBarsOnRight, then adds the morph at the back if true, otherwise it is added in front. But flopout hScrollbars need the lowerpanes to be behind the upper ones in the draw order. Hence the value of scrollBarsOnRight affects the order in which the lowerpanes are added. "	Preferences scrollBarsOnRight ifFalse: [		self 			addLowerPanesTo: window 			at: (0@hSepFrac corner: 1@1) 			with: editString].			window 		addMorph: self buildMorphicSystemCatList		frame: (0@0 corner: 0.25@hSepFrac).	self 		addClassAndSwitchesTo: window 		at: (0.25@0 corner: 0.5@hSepFrac)		plus: 0.	window 		addMorph: self buildMorphicMessageCatList		frame: (0.5@0 extent: 0.25@hSepFrac).	window addMorph: self buildMorphicMessageList		frame: (0.75@0 extent: 0.25@hSepFrac).			Preferences scrollBarsOnRight ifTrue: [		self 			addLowerPanesTo: window 			at: (0@hSepFrac corner: 1@1) 			with: editString].			window setUpdatablePanesFrom: #(systemCategoryList classList messageCategoryList messageList).	^ window! !!DualChangeSorter class methodsFor: 'window color' stamp: 'cbr 11/15/2010 19:44'!windowColor	^ ColorTheme current dualChangeSorter! !!LazyListMorph methodsFor: 'drawing' stamp: 'cbr 11/15/2010 19:46'!colorForRow: 	row	^(selectedRow notNil and: [ row = selectedRow])		ifTrue: [ ColorTheme current listRow ]		ifFalse: [ self color ].! !!LazyListMorph methodsFor: 'drawing' stamp: 'cbr 11/15/2010 19:46'!drawSelectionOn: aCanvas	| selectionDrawBounds |	selectedRow ifNil: [ ^self ].	selectedRow = 0 ifTrue: [ ^self ].	selectionDrawBounds := self drawBoundsForRow: selectedRow.	selectionDrawBounds := selectionDrawBounds intersect: bounds.	aCanvas fillRectangle: selectionDrawBounds color: (		Display depth = 1 			ifTrue: [Color lightGray] 			ifFalse: [				Display depth = 2 					ifTrue: [Color black] 					ifFalse: [ColorTheme current listHighlight]])! !!ListItemWrapper methodsFor: 'as yet unclassified' stamp: 'cbr 11/15/2010 19:45'!highlightingColor	^ ColorTheme current text! !!ListLW methodsFor: 'initialization' stamp: 'cbr 11/15/2010 19:45'!initialize	super initialize.	backColor _ ColorTheme current listHighlight.	scrollBarColor _ backColor darker.	scrollThumbColor _ scrollBarColor darker! !!MinimalStringMorph methodsFor: 'initialization' stamp: 'cbr 11/15/2010 19:46'!defaultColor	"answer the default color/fill style for the receiver"	^ ColorTheme current text! !!OneLineEditorMorph methodsFor: 'initialization' stamp: 'cbr 11/15/2010 19:46'!defaultColor	"answer the default color/fill style for the receiver"	^ ColorTheme current text! !!PluggableButtonMorph methodsFor: 'drawing' stamp: 'cbr 11/15/2010 19:46'!drawOn: aCanvas 	| w f center x y |	super drawOn: aCanvas.	f _ self fontToUse.	center _ bounds center.	label ifNotNil: [		w _ f widthOfString: label.		x _ bounds width > w			ifTrue: [ center x - (w // 2) ]			ifFalse: [ bounds left +4].		y _ center y - (f height // 2).		aCanvas drawString: label at: x@y font: f color: ColorTheme current label ].	showSelectionFeedback ifTrue: [		aCanvas frameRectangle: self innerBounds width: 2 color: feedbackColor].! !!PluggableButtonMorph methodsFor: 'initialization' stamp: 'cbr 11/15/2010 19:46'!initialize	"initialize the state of the receiver"	super initialize.	model _ nil.	getStateSelector _ nil.	actionSelector _ nil.	getLabelSelector _ nil.	getMenuSelector _ nil.	shortcutCharacter _ nil.	askBeforeChanging _ false.	triggerOnMouseDown _ false.	onColor _ ColorTheme current buttonOn.	offColor _ ColorTheme current buttonOff.	feedbackColor _ Color red.	showSelectionFeedback _ false.	allButtons _ nil.	argumentsProvider _ nil.	argumentsSelector _ nil.	self extent: 20 @ 15! !!PluggableListMorph methodsFor: 'initialization' stamp: 'cbr 11/15/2010 19:57'!model: anObject listGetter: getListSel indexGetter: getSelectionSel indexSetter: setSelectionSel menuGetter: getMenuSel keystrokeAction: keyActionSel	self model: anObject.	getListSelector _ getListSel.	getIndexSelector _ getSelectionSel.	setIndexSelector _ setSelectionSel.	getMenuSelector _ getMenuSel.	keystrokeActionSelector _ keyActionSel.	autoDeselect _ true.	self 		borderWidth: 1;		updateList;		selectionIndex: self getCurrentSelectionIndex;		initForKeystrokes! !!PluggableListMorph methodsFor: 'initialization' stamp: 'cbr 11/15/2010 19:46'!textColor	""	^ ColorTheme current text! !!Preferences class methodsFor: 'menu parameters' stamp: 'cbr 11/15/2010 19:45'!restoreDefaultMenuParameters	"Restore the four color choices of the original implementors of  	MorphicMenus"	" 	Preferences restoreDefaultMenuParameters	"	Parameters			at: #menuColor		put: ColorTheme current menu;				at: #menuBorderWidth		put: 2;			at: #menuTitleColor		put: ColorTheme current menuTitleBar;			at: #menuTitleBorderWidth		put: 1! !!Preferences class methodsFor: 'standard queries' stamp: 'cbr 11/15/2010 19:45'!focusIndicatorColor	^ self		valueOfFlag: #focusIndicatorColor		ifAbsent: [ColorTheme current focusIndicator]! !!Preferences class methodsFor: 'text highlighting' stamp: 'cbr 11/15/2010 19:45'!initializeTextHighlightingParameters	"	Preferences initializeTextHighlightingParameters	"	Parameters		at: #insertionPointColor		put: ColorTheme current insertionPoint;		at: #textHighlightColor		put: ColorTheme current textHighlight.! !!SHTextStylerST80 class methodsFor: 'style table' stamp: 'cbr 11/15/2010 19:45'!defaultStyleTable	^ ColorTheme current shout! !!SimpleHierarchicalListMorph methodsFor: 'accessing' stamp: 'cbr 11/15/2010 19:46'!lineColor	"One line color for the whole system."		lineColor ifNotNil: [ ^lineColor ].	^ ColorTheme current line! !!SimpleHierarchicalListMorph methodsFor: 'drawing' stamp: 'cbr 11/15/2010 19:46'!drawOn: aCanvas	super drawOn: aCanvas.	(drawKeyboardFocusIndicator and: [ self hasKeyboardFocus ]) ifTrue: [		aCanvas			frameRectangle: self focusIndicatorRectangle 			width: Preferences focusIndicatorWidth			color: Preferences focusIndicatorColor ].	selectedMorph  ifNotNil:		[aCanvas fillRectangle:			((selectedMorph bounds: selectedMorph bounds in: self)						intersect: scroller bounds)				color: ColorTheme current listHighlight].	Preferences showLinesInHierarchyViews ifTrue:[		self drawLinesOn: aCanvas.	].! !!StringHolder class methodsFor: 'window color' stamp: 'cbr 11/15/2010 19:44'!windowColor	^ ColorTheme current workspace! !!Browser class methodsFor: 'window color' stamp: 'cbr 11/15/2010 19:44'!windowColor	^ ColorTheme current browser! !!ChangeSorter class methodsFor: 'window color' stamp: 'cbr 11/15/2010 19:44'!windowColor	^ ColorTheme current changeSorter! !!FileList class methodsFor: 'window color' stamp: 'cbr 11/15/2010 19:44'!windowColor	^ ColorTheme current fileList! !!PackagePaneBrowser class methodsFor: 'window color' stamp: 'cbr 11/15/2010 19:44'!windowColor	^ ColorTheme current packageBrowser! !!StringMorph methodsFor: 'initialization' stamp: 'cbr 11/15/2010 19:45'!defaultColor	"answer the default color/fill style for the receiver"	^ ColorTheme current menuText! !!MenuItemMorph methodsFor: 'drawing' stamp: 'cbr 11/15/2010 19:46'!drawOn: aCanvas 	| stringColor stringBounds leftEdge |	stringColor := color.	isSelected & isEnabled		ifTrue: [			aCanvas fillRectangle: bounds color: (				Display depth < 8					ifTrue: [ Color veryLightGray ]					ifFalse: [ ColorTheme current menuHighlight ])].	leftEdge := 0.	self hasIcon		ifTrue: [| iconForm | 			iconForm := isEnabled ifTrue:[self icon] ifFalse:[self icon asGrayScale].			aCanvas paintImage: iconForm at: self left @ (self top + (self height - iconForm height // 2)).			leftEdge := iconForm width + 2].	self hasMarker		ifTrue: [ leftEdge := leftEdge + self submorphBounds width + 8 ].	stringBounds := bounds left: bounds left + leftEdge.	aCanvas		drawString: contents		in: stringBounds		font: self fontToUse		color: stringColor.	subMenu		ifNotNil: [aCanvas paintImage: SubMenuMarker at: self right - 8 @ (self top + self bottom - SubMenuMarker height // 2)]! !!SystemWindow methodsFor: 'drawing' stamp: 'cbr 11/15/2010 19:46'!drawOn: aCanvas	| titleColor |	super drawOn: aCanvas.	titleColor _ self isTopWindow		ifTrue: [ self paneColorToUse paler ]		ifFalse: [ self paneColorToUse ].	aCanvas		frameAndFillRectangle: self titleAreaRect		fillColor: titleColor		borderWidth: borderWidth		borderColor: titleColor.	aCanvas		drawString: labelString		in: self labelRectangle		font: Preferences windowTitleFont		color: ColorTheme current label! !!SystemWindow methodsFor: 'initialization' stamp: 'cbr 11/4/2010 13:21'!createBox	"create a button with default to be used in the label area"	| box |	box := IconicButton new.	box		color: Color transparent;		borderNormal;		target: self.			^box! !!SystemWindow methodsFor: 'initialization' stamp: 'cbr 11/7/2010 18:58'!defaultColor	"answer the default color/fill style for the receiver"	^ Color white! !!SystemWindow methodsFor: 'initialization' stamp: 'cbr 11/15/2010 20:06'!initialize	"Initialize a system window. Add label, stripes, etc., if desired"	super initialize.	labelString ifNil: [labelString := 'Untitled Window' translated].	isCollapsed := false.	paneMorphs := Array new.	borderColor := #raised.	borderWidth := 2.		self wantsLabel ifTrue: [self initializeLabelArea].	self 		on: #mouseEnter		send: #spawnReframeHandle:		to: self.	self 		on: #mouseLeave		send: #spawnReframeHandle:		to: self.	self extent: 300 @ 200.	updatablePanes := Array new! !!SystemWindow methodsFor: 'initialization' stamp: 'cbr 11/4/2010 14:48'!initializeLabelArea	"Initialize the label area (titlebar) for the window."	| spacing |	spacing _ self boxExtent x + 2.	self		addMorph: (self createCloseBox position: 2@2);		addMorph: (self createCollapseBox position: spacing+2@2);		addMorph: (self createExpandBox position: spacing*2+2@2);		addMorph: (self createMenuBox position: spacing*3+2@2)! !!SystemWindow methodsFor: 'panes' stamp: 'cbr 11/15/2010 20:07'!paneColor		| cc |	(cc := paneColor) ifNotNil: [^cc].	Display depth > 2 		ifTrue: [			model ifNotNil: [					model isInMemory 						ifTrue: [							cc := self windowColor.]].			cc ifNil: [cc := paneMorphs isEmptyOrNil ifFalse: [paneMorphs first color]]].	cc ifNil: [cc := Color white].	self paneColor: cc.	^cc! !!SystemWindow methodsFor: 'panes' stamp: 'cbr 11/15/2010 19:46'!paneColor: aColor	paneColor _ aColor.	aColor		ifNotNil: [ ColorTheme current paneExperiment value: self value: aColor ].	self adoptPaneColor: aColor.! !!SystemWindow methodsFor: 'panes' stamp: 'cbr 11/14/2010 01:11'!windowColor	^ Color colorFrom: model class windowColor duller! !!SystemWindow class methodsFor: 'accessing - icons' stamp: 'cbr 11/4/2010 11:21'!closeIcon	"Supplied here because we don't necessarily have ComicBold"	CloseIcon ifNil: [		CloseIcon := (Form			extent: 14@14			depth: 32			fromArray: #(0 0 6032910 288687365 1550655006 2844865845 3450028610 3450028610 2828088372 1550655006 288687365 5967374 0 0 1377027 15091784 844830230 3063889984 4158092147 4294153621 4294681250 4294681250 4294153364 4158091376 3047046718 844764694 14763847 1376770 7742243 861541910 3585164870 4294282123 4294943908 4294943908 4294943651 4294943651 4294943908 4294943908 4294216330 3585099848 844764694 7676450 288098055 3047109684 4294211447 4294939539 4293757578 4293363848 4294939025 4294939282 4293954186 4293560456 4294939539 4294212732 3047110455 288031748 1584209695 4158081610 42