Color ifNil: [ aColor ].	insideColor = Color black ifFalse: [ | topColor |		topColor _ insideColor alphaMixed: 0.25 with: Color black.		font installOn: port foregroundColor: topColor.		font displayString: aString asString on: port 			from: firstIndex to: lastIndex at: (bounds topLeft + origin) kern: font baseKern negated ].	insideColor = Color white ifFalse: [ | bottomColor |		bottomColor _ insideColor alphaMixed: 0.22 with: Color white.		font installOn: port foregroundColor: bottomColor.		font displayString: aString asString on: port 			from: firstIndex to: lastIndex at: (bounds topLeft + origin + (0@2)) kern: font baseKern negated ].	font installOn: port foregroundColor: insideColor.	font displayString: aString asString on: port 		from: firstIndex to: lastIndex at: (bounds topLeft + origin + (0@1)) kern: font baseKern negated.	port clipRect: portRect.! !