'From Cuis 2.9 of 5 November 2010 [latest update: #634] on 24 November 2010 at 8:16:48 am'!!Debugger methodsFor: 'initialize' stamp: 'jmv 11/24/2010 08:12'!buttonRowForPreDebugWindow	| row aButton quads buttons |	buttons _ OrderedCollection new.	quads _ OrderedCollection withAll: self preDebugButtonQuads.	(self interruptedContext selector == #doesNotUnderstand:) ifTrue: [		quads add: { 'Create'. #createMethod. #magenta. 'create the missing method' }	].	quads do: [ :quad |		aButton _ SimpleButtonMorph new target: self.		aButton color: Color transparent.		aButton actionSelector: quad second.		aButton label: quad first.		aButton color: (Color colorFrom: quad third) muchLighter.		aButton setBalloonText: quad fourth.		buttons add: aButton].		row _ AlignmentMorph proportional.	row addInRow: buttons.	^row! !!Debugger methodsFor: 'initialize' stamp: 'jmv 11/24/2010 08:16'!preDebugButtonQuads	^{	{'Proceed'.		#proceed. 	#blue. 	'continue execution'}.	{'Abandon'.		#abandon. 	#black.	'abandon this execution by closing this window'}.	{'Debug'.		#debug.		#red. 		'bring up a debugger'}}! !!Debugger methodsFor: 'context stack menu' stamp: 'jmv 11/24/2010 08:13'!buildMorphicNotifierLabelled: label message: messageString 	| notifyPane window contentTop extentToUse |	self expandStack.	window := (SystemWindow labelled: label) model: self.	contentTop := 0.2.	extentToUse := 650 @ 320.	"nice and wide to show plenty of the error msg"	window addMorph: self buttonRowForPreDebugWindow		frame: (0 @ 0 corner: 1 @ contentTop).	messageString 		ifNil: [			notifyPane := PluggableListMorph 						model: self						listGetter: #contextStackList						indexGetter: #contextStackIndex						indexSetter: #debugAt:						menuGetter: nil						keystrokeAction: nil]		ifNotNil: [			notifyPane := PluggableTextMorph 						model: self						editorClass: self editorClass						textGetter: nil						textSetter: nil						selectionGetter: nil						menuGetter: #debugProceedMenu:.			notifyPane				editString: messageString;				askBeforeDiscardingEdits: false].	window addMorph: notifyPane frame: (0 @ contentTop corner: 1 @ 1).	^window openInWorldExtent: extentToUse! !Debugger removeSelector: #buttonRowForPreDebugWindow:!Smalltalk removeClassNamed: #PreDebugWindow!